/* page for registering for account */

import React, { useState } from "react";
import axios from "axios";

export default function Register() {
  const [confirmPassword, setconfirmPassword] = useState(""); // used to check if the confirm password is equal to password
  const [showCard1, setShowCard1] = useState(false);

  const [user, setUser] = useState({
    //u_id: 0, // You can leave this blank if it's auto-generated by the backend
    u_firstname: "",
    u_lastname: "",
    u_name: "",
    u_pwd: "",
    u_email: "d",
    u_phone: "", // Initialize with a default value or leave it blank
  });

  async function handleSubmit(e) {
    if (user.u_firstname === "") {
      alert("Invalid firstname");
      return;
    }
    if (user.u_lastname === "") {
      alert("Invalid lastname");
      return;
    }
    if (user.u_username === "") {
      alert("Invalid username");
      return;
    }
    if (user.u_pwd !== confirmPassword) {
      alert("Password does not match Confirm Password");
      alert(user.u_password);
      alert(confirmPassword);
      return;
    }
    if (user.u_pwd.length < 8) {
      alert("Invalid Password");
      return;
    }
    if (user.u_email === "") {
      alert("Invalid Email");
      return;
    }
    console.log(user);
    e.preventDefault();
    try {
      const emailExistsResponse = await axios.get(
        `http://localhost:8080/api/user/findUserByEmail?u_email=${user.u_email}`
      );

      const userExists = emailExistsResponse.data;

      if (userExists) {
        alert("User with the same email exists");
      } else {
        // User with the same email doesn't exist, proceed to add the user
        const addUserResponse = await axios.post(
          "http://localhost:8080/api/user/add",
          user
        );

        if (addUserResponse.status === 200) {
          console.log("User added successfully");
          const emailExistsResponse = await axios.post(
            `http://localhost:8080/api/email/send-verification-email?u_email=${user.u_email}`
          );
          if (emailExistsResponse.status === 200) {
            alert("Email sent");
          }
          // You can add further actions if the user is added successfully
        } else {
          console.error("Failed to add user");
          alert("User Failed to Add");
        }
      }
    } catch (error) {
      console.error("An error occurred:", error);
      alert("ERROR");
    }
  }

  function verifyCard1() {
  }
  function verifyCard2() {}
  function verifyCard3() {}

  function showCard() {
    setShowCard1(true);
  }

  return (
    <body className="register">
      <h1>Join ABC </h1>
      <form>
        <div style={{ paddingBottom: "20px" }}>
          <label>
            First Name:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_firstname: event.target.value,
                });
                console.log(user.u_firstname);
              }}
              type="text"
              name="u_firstname"
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Last Name:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_lastname: event.target.value,
                });
              }}
              type="text"
              name="u_lastname"
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Username:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_name: event.target.value,
                });
              }}
              type="text"
              name="u_name"
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Email:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_email: event.target.value,
                });
              }}
              type="text"
              name="u_email"
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Password:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_pwd: event.target.value,
                });
              }}
              type="password"
              minLength="8"
              name="u_pwd"
            />
            (8 Characters min)
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Confirm Password:
            <input
              type="password"
              minLength="8"
              onChange={(e) => setconfirmPassword(e.target.value)}
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Phone Number:
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_phone: event.target.value,
                });
              }}
              type="text"
              name="u_phone"
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Receive Promotion <input type="checkbox" defaultChecked={true} />
          </label>
        </div>
        
        {(showCard1 == false) &&
        <button onClick={showCard}> Add </button>
        }

        {(showCard1 == true) &&

        <div style={{ paddingBottom: "20px" }}>
          <p><button onClick={showCard}> delete </button></p>
          <label>
            Phone Number:
            <input
              onChange={(event) => {
                
              }}
              type="text"
              name="u_phone"
            />
          </label>
        </div>
        }
        

        <div style={{ paddingBottom: "20px" }}>
          <button className="submit-btn" onClick={handleSubmit}>
            Submit
          </button>
        </div>
      </form>
    </body>
  );
}
