/* page for listing promo */

import { useState, useEffect } from "react";
import "./ManageMovies.css";
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { CardActionArea } from '@mui/material';

export default function PromoList({ Data }) {
  const [map_data, setmap_data] = useState(Data);

  useEffect(() => {
    // Initialize map_data when Data changes
    setmap_data(Data);
  }, [Data]);

  return (
    <>
      <div className="whole-map">
        {map_data.map((e) => (
          <div key={e.p_id} className="map-data">
            <Card sx={{ maxWidth: 345, maxHeight:360 }}>
      <CardActionArea>
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
            <div className="map-name">
              <p className="">{e.code}</p>
            </div>
          </Typography>
          <Typography variant="body2" color="text.secondary">
            {e.discount}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
            
          </div>
        ))}
      </div>
    </>
  );
}
