/* page for login in an account */
import React, { useState } from 'react';


export default function Login(prop) {

    
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    function handleSubmit(e) {
        

        e.preventDefault();
        if (email === 'admin@gmail.com' && password === '12345678') {
            
            alert('logged in') 
            prop.isAdmin()
            prop.submit()
            window.location.assign("http://localhost:3000/adminHome");
            return
        }
        else if (email === 'customer@gmail.com' && password === '12345678') {
            alert('logged in')
            prop.isCustomer()
            prop.submit()
            window.location.assign("http://localhost:3000/");

            return
        } 
        else {
            alert('Invalid Login')
        }
    }


    return (
        <body className= "reg"> 
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>

                    
                    <div style={{paddingBottom:'20px'}}>
                    <label> 
                        Email: <input 
                        type ="email" 
                        name="email"
                        onChange={(e) => setEmail(e.target.value)}
                        /> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label> 
                        Password: <input 
                        type="password"
                        name="password"
                        onChange={(e) => setPassword(e.target.value)}
                        /> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label className="submit-btn" onClick={handleSubmit}>Submit</label>
                    </div>

                    <a href = "/register">Don't have an account yet? Sign Up</a>
                    
            </form>
        </body>
        
    )
}
