import * as React from "react";
import axios from "axios";
import { styled } from "@mui/material/styles";
import Button from "@mui/material/Button";
import CloudUploadIcon from "@mui/icons-material/CloudUpload";
import FormGroup from "@mui/material/FormGroup";
import FormControlLabel from "@mui/material/FormControlLabel";
import Checkbox from "@mui/material/Checkbox";
import { lightBlue } from "@mui/material/colors";
import { useState } from "react";

export default function AddMovie() {
  const [movie, setMovie] = useState({
    m_id: 0, // You can leave this blank if it's auto-generated by the backend
    m_isbn: "",
    m_title: "",
    m_director: "",
    m_producer: "",
    m_poster: "",
    m_image1: "",
    m_image2: "",
    m_price: "", // Initialize with a default value or leave it blank
    mt_id: 0, // Initialize with a default value or leave it blank
    m_stock: 0, // Initialize with a default value or leave it blank
    m_mark: "",
  });
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setMovie({
      ...movie,
      [name]: value,
    });
  };

  const [description, setDescription] = useState("");
  const [trailer, setTrailer] = useState("");
  const [showing, setShowing] = useState(false);

  async function handleSubmit(e) {
    e.preventDefault();
    // window.location.assign("http://localhost:3000/manage");
    // alert("Movie Added")
    try {
      const response = await axios.post(
        "http://localhost:8080/api/movie/add",
        movie
      );

      if (response.status === 200) {
        // Movie added successfully
        console.log("Movie added successfully");
      } else {
        // Handle errors
        console.error("Failed to add movie");
      }
    } catch (error) {
      console.error("An error occurred:", error);
    }
  }

  return (
    <body className="reg">
      <h1>Add Movie</h1>
      <form onSubmit={handleSubmit}>

        <div className="input-group">
          Movie Title
          <label htmlFor="movieTitle"></label>
          <input
            type="text"
            name="m_title"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Movie ISBN
          <label htmlFor="movieIsbn"></label>
          <input
            type="text"
            name="m_isbn"
            onChange={(event) => {
              setIsbn({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Producer
          <label htmlFor="producer"></label>
          <input
            type="text"
            name="m_producer"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Director
          <label htmlFor="director"></label>
          <input
            type="text"
            name="m_director"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Cast
          <label htmlFor="cast"></label>
          <input
            type="text"
            name="m_isbn"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Poster URL
          <label htmlFor="poster"></label>
          <input
            type="text"
            name="m_poster"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Image1 URL
          <label htmlFor="image1"></label>
          <input
            type="text"
            name="m_image1"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Image2 URL
          <label htmlFor="image2"></label>
          <input
            type="text"
            name="m_image2"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          />
        </div>

        <div className="input-group">
          Trailer URL
          <label htmlFor="trailer"></label>
          <input
            type="text"
            onChange={(event) => {
              setTrailer(event.target.value);
            }}
          />
        </div>

        <div>
          Rating
          <select
            name="m_mark"
            onChange={(event) => {
              setMovie({
                ...movie,
                name: event.target.value,
              });
            }}
          >
            <option value="G">G</option>
            <option value="PG-13">PG-13</option>
            <option value="R">R</option>
            <option value="Adult">18+</option>
          </select>
        </div>

        <div className="input-group">
          Description
          <label htmlFor="description"></label>
          <textarea
            row={5}
            cols={5}
            onChange={(event) => {
              setDescription(event.target.value);
            }}
          />
        </div>

        <div>
          <FormGroup>
            <FormControlLabel
              control={
                <Checkbox
                  onChange={(event) => {
                    setShowing(!showing);
                  }}
                  sx={{
                    color: lightBlue[800],
                    "&.Mui-checked": { color: lightBlue[600] },
                  }}
                />
              }
              label="Currenty Showing"
            />
          </FormGroup>
        </div>

        <button type="submit" className="submit-btn" onClick={handleSubmit}>
          Submit
        </button>
      </form>
    </body>
  );
}
