/* page for editing promo */

import axios from "axios";
import { useEffect, useState } from "react";
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';

export default function EditPromotion() {
    const [select, setSelected]  = useState('');
    const [optionList,setOptionList] = useState([]);
    const fetchData = () => {
        axios
          .get('http://localhost:8080/api/movie/getAll')
          .then((response) => {
            const { data } = response;
            if(response.status === 200){
                //check the api call is success by stats code 200,201 ...etc
                setOptionList(data)
            }else{
                //error handle section 
            }
          })
          .catch((error) => console.log(error));
      };

    useEffect(()=>{
        fetchData();
    },[])

    async function handleSubmit(e) {
        alert("Promotion Edited")
        window.location.assign("http://localhost:3000/editPromotion");
    }

    return (
    <body style={{backgroundColor: 'crimson'}}>
    <h1 style={{backgroundColor: 'crimson', fontSize: '30px', color: 'black'}}>Edit Promotions</h1>
    
    <form onSubmit={handleSubmit}>
        <h1 style={{color: 'black'}}>Select Promotion to Edit</h1>

        <div classname="registerformat">
            <select
                disabled={false}
                value={select}
                onChange={(e) => setSelected(e.currentTarget.value)}
            >
            {optionList.map((item) => (
            <option key={item.p_id}>
                {item.code}
            </option>
            ))}
            </select>
        </div>

        <div classname="registerformat" style={{ padding: "10px", color: 'black'}}>
            Discount Amount :
            <input type="number">
            </input>
            %
        </div>

        <div classname="registerformat" style={{ padding: "10px", color: 'black'}}>
            
            <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DemoContainer components={['DatePicker']}>
            <DatePicker label="Promotion Start Date" variant="solid"/>
            </DemoContainer>
            </LocalizationProvider>
        </div>

        <button type="submit" className="submit-btn" onClick={handleSubmit}>
          Submit
        </button>
    </form>


    </body>
)
}
