  // const memoedGetUser = _.memoize(fetchData);
  useEffect(() => {
    //console.log(localStorage.getItem("userId"));
    // memoedGetUser(localStorage.getItem("userId"));
    fetchData(localStorage.getItem("userId"));
    console.log(localStorage.getItem("userId"));
