    e.preventDefault();
    setField1(!showField1);
  }
  function removeCard_3(e) {
    /* remove credit card 3 */
    e.preventDefault();
    setField1(!showField1);
  }

  const [showField1, setField1] = useState(false);
  const [showField2, setField2] = useState(false);
  const [showField3, setField3] = useState(false);

  function addCard_1(e) {
    setField1(!showField1);
    /* add information to db */
  }
  function addCard_2(e) {
    setField1(!showField2);
    /* add information to db */
  }
  function addCard_3(e) {
    setField1(!showField3);
    /* add information to db */
  }

