        <div classname="payment-info" style={{ paddingBottom: "20px" }}>
          <p>{cardName_1}</p>
          {}
          <p>
            <button onClick={removeCard_1} type="submit" style={{}}>
              ADD
            </button>
          </p>
        </div>
