/* page for registering for account */

import React, { useState } from "react";
import axios from "axios";

export default function EditProfile() {
  const userExistsJSON = localStorage.getItem("CurrentUser");
  const userExists = JSON.parse(userExistsJSON);
  const [user, setUser] = useState({
    u_firstname: userExists.u_firstname,
    u_lastname: userExists.u_lastname,
    u_name: userExists.u_name,
    u_phone: userExists.u_phone,
    u_promo: userExists.u_promo,
  });
  async function handleSubmit(e) {
    let re =
      /^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
    /*
        if (u_firstname.length > 0) {
            alert('Invalid Password')
            return
        }
        if (u_lastname.length > 0) {
            alert('Invalid Password')
            return
        }
        if (u_username.length > 0) {
            alert('Invalid Password')
            return
        }
        if (re.test(u.email) == false) {
            alert('Invalid email')
            return
        }
        }
        */
    e.preventDefault();
    // const userData = await axios.get(
    //   `http://localhost:8080/api/user/getUserByEmail?u_email=${userExists.u_email}`
    // );
    // const user = userData.data;
    // await axios.post(`http://localhost:8080/api/user/change-profile-all`, user);
    await axios.post(
      `http://localhost:8080/api/user/change-profile?u_email=${userExists.u_email}&u_firstname=${user.u_firstname}&u_lastname=${user.u_lastname}&u_name=${user.u_name}&u_phone=${user.u_phone}&u_promo=${user.u_promo}`
    );
    alert("Changes have been saved");
    window.location.assign("http://localhost:3000/profile");
  }

  function validateForm() {}

  return (
    <body className="register">
      <h1>Edit Profile </h1>
      <form onSubmit={handleSubmit}>
        <div style={{ paddingBottom: "20px" }}>
          <label>
            First Name:{" "}
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_firstname: event.target.value,
                });
              }}
              type="name"
              defaultValue={userExists.u_firstname}
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Last Name:{" "}
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_lastname: event.target.value,
                });
              }}
              type="name"
              defaultValue={userExists.u_lastname}
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Username:{" "}
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_name: event.target.value,
                });
              }}
              type="username"
              defaultValue={userExists.u_name}
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Phone Number:{" "}
            <input
              onChange={(event) => {
                setUser({
                  ...user,
                  u_phone: event.target.value,
                });
              }}
              type="tel"
              defaultValue={userExists.u_phone}
            />
          </label>
        </div>

        <h1 style={{ paddingBottom: "10px" }}>Home Address</h1>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Street: <input type="name" defaultValue={userExists.u_address} />
          </label>
        </div>
        <div style={{ paddingBottom: "20px" }}>
          <label>
            Zip: <input type="name" defaultValue={userExists.credit_zip1} />
          </label>
        </div>
        <div style={{ paddingBottom: "20px" }}>
          <label>
            State: <input type="name" defaultValue={userExists.credit_state1} />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label>
            Receive Promotion{" "}
            <input
              onChange={(event) => {
                const value = event.target.checked ? 1 : 0;
                setUser({
                  ...user,
                  u_promo: value,
                });
              }}
              type="checkbox"
              defaultChecked={userExists.u_promo}
            />
          </label>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label type="submit" className="submit-btn" onClick={handleSubmit}>
            Confirm Changes
          </label>
        </div>
      </form>
    </body>
  );
}
