/* page for adding promo */
import * as React from 'react';
import axios from "axios";
import { useEffect, useState } from "react";
import { DemoContainer } from '@mui/x-date-pickers/internals/demo';
import { AdapterDayjs } from '@mui/x-date-pickers/AdapterDayjs';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { DatePicker } from '@mui/x-date-pickers/DatePicker';

export default function AddPromotion() {
    const [select, setSelected]  = useState('');
    const [optionList,setOptionList] = useState([]);
    const fetchData = () => {
        axios
          .get('http://localhost:8080/api/movie/getAll')
          .then((response) => {
            const { data } = response;
            if(response.status === 200){
                //check the api call is success by stats code 200,201 ...etc
                setOptionList(data)
            }else{
                //error handle section 
            }
          })
          .catch((error) => console.log(error));
      };

    useEffect(()=>{
        fetchData();
    },[])

    async function handleSubmit(e) {
        alert("Promotion Added")
        window.location.assign("http://localhost:3000/addPromotion");
    }
    return (
    
    <body style={{backgroundColor: 'crimson'}}>
    <h1 style={{color: 'black', fontSize: '40px'}}>Add Promo</h1>
    
    <form onSubmit={handleSubmit}>
        <h2 style={{color: 'black'}}>Promo Code</h2>
        <div classname="registerformat" style={{ padding: "10px", color: 'black'}}>
            Code :
            <input>
            </input>
        </div>

        <div classname="registerformat" style={{ padding: "10px", color: 'black'}}>
            Discount Amount :
            <input type="number">
            </input>
            %
        </div>

        <div classname="registerformat" style={{ padding: "10px", color: 'black'}}>
            
            <LocalizationProvider dateAdapter={AdapterDayjs}>
            <DemoContainer components={['DatePicker']}>
            <DatePicker label="Promotion Start Date" variant="solid"/>
            </DemoContainer>
            </LocalizationProvider>
        </div>

        <button type="submit" className="submit-btn" onClick={handleSubmit}>
          Submit
        </button>
    </form>


    </body>
    
    
    )
}
