import React, { useState } from "react";
import axios from "axios";

export default function Verify() {
  const [code, setCode] = useState("");
  async function handleSubmit(e) {
    console.log(localStorage.getItem("userId"));
    const userExistsJSON = localStorage.getItem("userId");
    const userExists = JSON.parse(userExistsJSON);
    const userExistsResponse = await axios.get(
      `http://localhost:8080/api/user/findUserWithId?u_id=${userExists.u_id}`
    );
    const bool = await axios.post(
      `http://localhost:8080/api/email/verify?u_email=${userExists.u_email}&code=${code}`
    );
    if (bool) {
      alert("Account Verified");
      window.location.assign("http://localhost:3000/login");
    }
  }
  return (
    <body className="reg">
      <label>
        Enter verify code here:
        <input
          onChange={(event) => {
            setCode(event.target.value);
          }}
          type="text"
          name="code"
        />
      </label>
      <label type="button" className="submit-btn" onClick={handleSubmit}>
        Submit
      </label>
      <div style={{ paddingTop: "20px" }}>
        <a href="/login">Go to Login</a>
      </div>
    </body>
  );
}
