/* page for registering for account */

import React, { useState } from 'react';

export default function EditProfile() {

    const [confirmPassword, setconfirmPassword] = useState('') // used to check if the confirm password is equal to password

   
    function handleSubmit(e) {

        /*
        if (u_firstname.length > 0) {
            alert('Invalid Password')
            return
        }
        if (u_lastname.length > 0) {
            alert('Invalid Password')
            return
        }
        if (u_username.length > 0) {
            alert('Invalid Password')
            return
        }
        if (u_password.length != confirmPassword.length) {
            alert('Password does not match Confirm Password ')
            return
        }
        if (u_password.length >= 8) {
            alert('Invalid Password')
            return
        }
        if (u_email.length == 0) {
            alert('Invalid Email')
            return
        }
        */
        e.preventDefault();
        alert('Changes have been saved');
        window.location.assign("http://localhost:3000/profile");
    }
    
    function validateForm() {
        
    }

    return (

        
        <body className= "register"> 
            
            <h1>Edit Profile </h1>
            <form onSubmit={handleSubmit}>

                    <div style={{paddingBottom:'20px'}}>
                    <label > 
                        First Name: <input type ="name"/> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label> 
                        Last Name: <input type ="name"/> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label> 
                        Username: <input type ="username"/> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label> 
                        Phone Number: <input type="tel"/> 
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label>
                         Receive Promotion <input type="checkbox" defaultChecked={true} />
                    </label>
                    </div>

                    <div style={{paddingBottom:'20px'}}>
                    <label type="submit" className="submit-btn" onClick={handleSubmit}>Submit</label>
                    </div>
            </form>
        </body>
        
    )
}
