/* page for adding Admins */

import axios from "axios";
import { useEffect, useState } from "react";
export default function AddAdmin() {
    const [select, setSelected]  = useState('');
    const [optionList,setOptionList] = useState([]);
    const fetchData = () => {
        axios
          .get('http://localhost:8080/api/movie/getAll')
          .then((response) => {
            const { data } = response;
            if(response.status === 200){
                //check the api call is success by stats code 200,201 ...etc
                setOptionList(data)
            }else{
                //error handle section 
            }
          })
          .catch((error) => console.log(error));
      };

    useEffect(()=>{
        fetchData();
    },[])

    async function handleSubmit(e) {
    }

    return (
    <body style={{backgroundColor: 'crimson'}}>
    <h1 style={{backgroundColor: 'crimson', fontSize: '30px', color: 'black'}}>Delete Movies</h1>
    
    <form onSubmit={handleSubmit}>
        <h1 style={{color: 'black'}}>Select User to Promote to Admin</h1>
        <div classname="registerformat">
            
            <select
                disabled={false}
                value={select}
                onChange={(e) => setSelected(e.currentTarget.value)}
            >
            {optionList.map((item) => (
            <option key={item.p_id}>
                {item.code}
            </option>
            ))}
            </select>
        </div>
            
        <button type="submit" className="submit-btn" onClick={handleSubmit}>
          Submit
        </button>
    </form>


    </body>
)
}
