/* page for login in an account */
import React, { useState } from "react";
import axios from "axios";

export default function Login(prop) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  // const [userId, setUserId] = useState(() => {
  //   // getting stored value
  //   const saved = localStorage.getItem("userId");
  //   const initialValue = parseInt(JSON.parse(saved));
  //   return initialValue || 0;
  // });

  async function handleSubmit(e) {
    e.preventDefault();
    const emailExistsResponse = await axios.get(
      `http://localhost:8080/api/user/getUserByEmail?u_email=${email}`
    );
    const userExists = emailExistsResponse.data;
    // console.log(userExists);
    // console.log(userExists.u_email);
    // console.log(userExists.u_pwd);
    // console.log(email);
    // console.log(password);
    // console.log(userExists.u_email === email);
    // console.log(userExists.u_pwd === password);
    // console.log(userExists.u_role);
    if (userExists) {
      localStorage.setItem("userId", userExists.u_id);
      localStorage.setItem("currentUser", userExists);
      console.log(localStorage.getItem("currentUser"));
      //console.log(userExists.u_id);
      if (
        email === userExists.u_email &&
        password === userExists.u_pwd &&
        userExists.u_role === 1
      ) {
        alert("logged in");
        prop.isAdmin();
        prop.submit();
        window.location.assign("http://localhost:3000/adminHome");
        return;
      } else if (
        email === userExists.u_email &&
        password === userExists.u_pwd &&
        userExists.u_role === 0
      ) {
        alert("logged in");
        prop.isCustomer();
        prop.submit();
        window.location.assign("http://localhost:3000/");
        return;
      } else {
        alert("Invalid Login");
      }
    } else {
      alert("Email does not have account connected with it");
    }
  }

  return (
    <body className="reg">
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <div style={{ paddingBottom: "20px" }}>
          <label>
            Email:{" "}
            <input
              type="email"
              name="email"
              onChange={(e) => setEmail(e.target.value)}
            />
          </label>
        </div>

        <div>
          <label>
            Password:{" "}
            <input
              type="password"
              name="password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </label>
          <a style={{ fontSize: 15 }} href="/forgotPass">
            {" "}
            Can't Remember?
          </a>
        </div>

        <div style={{ paddingBottom: "20px" }}>
          <label className="submit-btn" onClick={handleSubmit}>
            Submit
          </label>
        </div>

        <a href="/register">Don't have an account yet? Sign Up</a>
      </form>
    </body>
  );
}
